	public static function resizeCategoryPngs(){
		$dir = ImageHelper::PUBLIC_IMG_VARIABLE_CATEGORIES;
		$fileNames = FileFinder::listFilesAndFoldersNonRecursively(FileHelper::absPath($dir), false);
		$dimensions = [
			//16,
			32,
			64,
			128,
			//,
			//512
		];
		foreach($dimensions as $dimension){
			foreach($fileNames as $fileName){
				if(stripos($fileName, '.png') === false){
					continue;
				}
				$oldPath = FileHelper::absPath($dir . '/' . $fileName);
				$name = QMStr::before('.', $fileName);
				$ext = QMStr::after('.', $fileName);
				$newDir = FileHelper::absPath($dir . '/' . $dimension . '/');
				$newPath = $newDir . $name . '.' . $ext;
				try {
					$image = new ImageResize($oldPath);
					$image->resize($dimension, $dimension);
					FileHelper::createDirectoryIfNecessary($newDir);
					$image->save($newPath);
				} catch (Throwable $e) {
					QMLog::error($e->getMessage() . ": $fileName");
				}
			}
		}
	}
	/**
	 * @param string $variableName
	 * @return array|bool
	 * @throws RateLimitConnectorException
	 */
	public static function getDrugImages(string $variableName){
		$url = 'https://rximage.nlm.nih.gov/api/rximage/1/rxnav?name=' . urlencode($variableName);
		$result = APIHelper::callAPI('GET', $url);
		if($result === false){
			return false;
		}
		if(isset($result->nlmRxImages) && count($result->nlmRxImages)){
			return $result->nlmRxImages;
		}
		return null;
	}

    /**
     * @param int $percent
     * @return string
     */
	public static function gradeToFace(int $percent): string{
		$path = ImageHelper::RATING_100_FACE_RATING_BUTTON_100_DEPRESSED_PNG;
		switch(true) {
			case $percent > 90:
				$path = ImageHelper::RATING_100_FACE_RATING_BUTTON_100_ECSTATIC_PNG;
				break;
			case $percent > 80:
				$path = ImageHelper::RATING_100_FACE_RATING_BUTTON_100_HAPPY_PNG;
				break;
			case $percent > 70:
				$path = ImageHelper::RATING_100_FACE_RATING_BUTTON_100_OK_PNG;
				break;
			case $percent > 60:
				$path = ImageHelper::RATING_100_FACE_RATING_BUTTON_100_SAD_PNG;
		}
		$path = "https://static.quantimo.do/img/rating/google-colors/$path";
		return ImageHelper::getImageUrl($path);
	}
