	/**
	 * @return array
	 * @throws SecretException
	 */
	public static function generateImageListJson(): array{
		$files = S3Public::listFilesComplicated('img', [
				'img/study-images',
			]);
		$flat = [];
		$files = Arr::flatten($files);
		foreach($files as $file){
			$type = strtolower(QMStr::afterLast($file, '.'));
			$name = QMStr::afterLast($file, "/");
			$name = QMStr::before('.', $name);
			$one = [
				'url' => self::BASE_URL . str_replace('img/', '', $file),
				'name' => $name,
				'type' => $type,
				'category' => QMStr::between($file, "img/", "/"),
			];
			$flat['all'][] = $one;
			$flat[$type][] = $one;
		}
		foreach($flat as $type => $arr){
			S3Public::uploadJsonAndJsFiles("data/$type-images", $arr);
		}
		return $files;
	}
