	/**
	 * @param string $variableName
	 * @param float|null $dosage
	 * @return string|null
	 * @throws RateLimitConnectorException
	 */
	public static function getDrugImageUrl(string $variableName, float $dosage = null): ?string{
		$nlmRxImages = self::getDrugImages($variableName);
		if($nlmRxImages === false){
			return null;
		}
		if($nlmRxImages && count($nlmRxImages)){
			foreach($nlmRxImages as $nlmRxImage){
				if($dosage && !empty($nlmRxImage->name) && strpos($nlmRxImage->name, (string)$dosage) !== false){
					$imageUrl = $nlmRxImage->imageUrl;
				}
			}
		}
		if(isset($imageUrl)){
			return $imageUrl;
		}
		if(isset($nlmRxImage)){
			return $nlmRxImage->imageUrl;
		}
		return null;
	}
	/**
	 * @param string $path
	 * @return string
	 */
	public static function imageToBase64(string $path): string{
		$type = pathinfo($path, PATHINFO_EXTENSION);
		$data = file_get_contents($path);
		$base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);
		return $base64;
	}

