	/**
	 * @param string $type
	 * @param string $data
	 * @param string $alt
	 * @param string $title
	 * @param string $elementId
	 * @return string
	 */
	public static function imageDataToHtml(string $type, string $data, string $alt, string $title,
		string $elementId): string{
		if($type === HighchartExport::SVG){
			$imageHtml = $data;
		} else{
			// max-width: unset; width: unset; required to avoid blur from WP css
			$style = ImageHelper::CHART_IMAGE_STYLES; // DO NOT REMOVE THIS!!!
			$base64 = 'data:image/' . $type . ';base64,' . base64_encode($data);
			$imageHtml = '
                <img id="' . $elementId . '-image"
                    class="chart-img"
                    style="' . $style . '"
                    src="' . $base64 . '"
                    alt="' . $alt . '"
                    title="' . $title . '"/>';
		}
		if(stripos($imageHtml, "position: absolute") !== false){
			throw new \LogicException("Do not use position: absolute because all the images will pile on top of each other!");
		}
		return $imageHtml;
	}
