	/**
	 * @param string $path
	 * @return string
	 */
	public static function getImageUrl(string $path): string{
		if(strpos($path, "https") === 0){
			return $path;
		}
		$path = QMStr::removeIfFirstCharacter('/', $path);
		$url = self::BASE_URL . $path;
		$url = str_replace('https://static.quantimo.do/img/img/', 'https://static.quantimo.do/img/', $url);
		return $url;
	}
	public static function convertPngToJpg(string $pngPath){
		$image = imagecreatefrompng($pngPath);
		$bg = imagecreatetruecolor(imagesx($image), imagesy($image));
		imagefill($bg, 0, 0, imagecolorallocate($bg, 255, 255, 255));
		imagealphablending($bg, true);
		imagecopy($bg, $image, 0, 0, 0, 0, imagesx($image), imagesy($image));
		imagedestroy($image);
		$quality = 75; // 0 = worst / smaller file, 100 = better / bigger file
		$jpgPath = str_replace(".png", ".jpg", $pngPath);
		\App\Logging\ConsoleLog::info("Converting\n\t$pngPath\n\tto $jpgPath");
		imagejpeg($bg, $jpgPath, $quality);
		imagedestroy($bg);
	}
